import React, { useState, useEffect } from 'react';
import IpMacPairCard from './IpMacPairCard';

interface Result {
  ip: string;
  // Add other properties if needed
}

const ScanResults = () => {
  const [ipCount, setIpCount] = useState(0);
  // const [results, setResults] = useState<Result[]>([]);

  useEffect(() => {
    fetch('http://localhost:3001/api/nmap')
      .then(response => response.json())
      .then(results => {
        console.log(results);  // For debugging
        setIpCount(results.ipCount);
        setResults(results.data);
      })
      .catch(error => {
        console.error('Error:', error);
      });
  }, []);

  return (
    <div className="flex justify-center items-center h-screen bg-gradient-to-r from-indigo-500 to-sky-500 flex-wrap">
      <div className="text-center w-full">
        <h1>Scan Results</h1>
        <p>IP count: {ipCount}</p>
      </div>
      {results && results.map((result, index) => (
        <IpMacPairCard key={index} ip={result.ip} />
      ))}
    </div>
  );
};

export default ScanResults;
